module.exports = (function(){

	var path = require('path'),
		dir = path.resolve(__dirname,".."),
		tests = path.resolve(dir , "tests");

	return {

		fs: require('fs'),
		toolstack: require('toolstack'),
		http: require('http'),
		url: require('url'),
		path: path,

		root: function(name){
			return require(path.resolve(dir,name));
		},
		tests: function(name){
			return require(path.resolve(tests,name));
		}
	};

})();